import React, { useState } from "react";
import axios from "axios";
import "./UploadForm.css"; // Import CSS for styling

const UploadForm = () => {
  const [selectedFile, setSelectedFile] = useState(null);
  const [outputImage, setOutputImage] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  // Handle file selection
  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
    setOutputImage(null); // Reset output when a new file is selected
    setError(""); // Clear errors
  };

  // Handle file upload
  const handleUpload = async () => {
    if (!selectedFile) {
      setError("Please select an image first.");
      return;
    }

    const formData = new FormData();
    formData.append("file", selectedFile);
    setLoading(true);

    try {
      const response = await axios.post("http://127.0.0.1:5000/upload", formData);
      const outputFilename = response.data.processed_image;
      setOutputImage(`http://127.0.0.1:5000/output/${outputFilename}`);
    } catch (error) {
      setError("Error processing image. Please try again.");
      console.error("Upload Error:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="upload-container">
      <h2>Sign Language Detection</h2>
      
      <input type="file" accept="image/*" onChange={handleFileChange} />
      <button onClick={handleUpload} disabled={loading}>
        {loading ? "Processing..." : "Upload & Detect"}
      </button>

      {error && <p className="error">{error}</p>}

      {outputImage && (
        <div className="result">
          <h3>Detected Image:</h3>
          <img src={outputImage} alt="Detected Sign" className="output-image" />
        </div>
      )}
    </div>
  );
};

export default UploadForm;
